// Get the AI agent's output string
const data = items[0].json.output;

let parsed;
try {
  // Try to extract JSON from markdown code blocks
  const jsonMatch = data.match(/```json\s*([\s\S]*?)\s*```/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[1]);
  } else {
    // If no markdown blocks, try to find raw JSON
    const start = data.indexOf('{');
    const end = data.lastIndexOf('}');
    if (start !== -1 && end !== -1) {
      parsed = JSON.parse(data.substring(start, end + 1));
    } else {
      throw new Error("No JSON found in output");
    }
  }
} catch (error) {
  throw new Error(`Failed to parse JSON: ${error.message}`);
}

// Return required fields
return [
  {
    json: {
      title: parsed.title,
      description: parsed.description,
      start_time: parsed.start_time,
      end_time: parsed.end_time,
      attendee_name: parsed.attendee_name,
      attendee_email: parsed.attendee_email || "",
      attendee_number: parsed.attendee_number,
      sdr_email: parsed.sdr_email || "",
      resume_url: parsed.resume_url
    }
  }
];

