const istString = now.toLocaleString("en-US", {
  timeZone: "Asia/Kolkata",
  weekday: 'long',
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  hour12: false
});

